$(document).ready(function(){

  // Ajout pour les vidÃ©os externes
  if($('#video-externe-view').length > 0 || $('#audio-view').length > 0 || $('#diaporama-view').length > 0){
    $('#Header').css("display","none");
    $('.accroche').css("display","none");
    $('.fiche-interet').css("display","none");
    $('.plus-info').css("display","none");
    $('.bloc-plus-image-ouvert').css("display","none");
    $('.quizz-fiche-interet').css("display","none");
    $('.bloc-carte-google').css("display","none");
    $('#breadcrumbs').css("display","none");
    $('.recherche').css("display","none");
    $('.copyright').css("display","none");
    $('.google-map-ferme-circuit-pieds-page').css("display","none");
    $('.plus-image-ouvert-pieds-page').css("display","none");
    $('.quizz-pictos-pieds-page').css("display","none");
    $(".pieds-page ul").css("max-width","60px");
    $('#container').css("background-color","#EEEEEE")
  }

  for(var j=0 ; j<$(".pieds-page ul").length;j++){
    if($(".pieds-page ul:eq("+j+")").css('display') != 'none' ){
    var list_length=$(".pieds-page ul:eq("+j+") li").length;
        for(var i=0 ; i<$(".pieds-page ul li").length;i++){
            if($(".pieds-page ul li:eq("+i+") a").css('display') == 'none' ){
                list_length=list_length-1;
            }
        }
        }
    }
    $(".pieds-page ul").css("max-width",list_length*62+'px');

                $(".voir-image").click(function() {
                      $(".liste-medias").show();
                      $(".plus-image").css("display","none");
                      $(".plus-image-ouvert").css("display","block");
                      $(".bloc-plus-image").addClass("bloc-plus-image-ouvert");
                      $(".accroche-plus-image").addClass("bloc-plus-image-ouvert");
                      $(".afficher-bloc-image").css("display","none");
                      $(".masquer-bloc-image").css("display","block");
                      $(".plus-image-pieds-page").css("display","none");
                      $(".plus-image-ouvert-pieds-page").css("display","block");
                      return false;
                });

                $(".masquer-image").click(function() {
                      $(".liste-medias").hide();
                      $(".plus-image").css("display","block");
                      $(".plus-image-ouvert").css("display","none");
                      $(".bloc-plus-image").removeClass("bloc-plus-image-ouvert");
                      $(".accroche-plus-image").removeClass("bloc-plus-image-ouvert");
                      $(".afficher-bloc-image").css("display","block");
                      $(".masquer-bloc-image").css("display","none");
                      $(".plus-image-pieds-page").css("display","block");
                      $(".plus-image-ouvert-pieds-page").css("display","none");
                      return false;
                });

                 $(".voir-info").click(function() {
                   $(".plus-info-detail").show();
                      $(".lien-plus-info").css("display","none");
                      $(".lien-plus-info-ouvert").addClass("plus-info-ouvert");
                      $(".lien-plus-info-ouvert").css({"display" : "block"});
                      $(".plus-info").addClass("plus-info-ouvert");
                      $(".accroche-plus-info").addClass("plus-info-ouvert");
                      $(".afficher-bloc-info").css("display","none");
                      $(".masquer-bloc-info").css("display","block");
                      $(".plus-info-pictos-pieds-page").css("display","none");
                      $(".lien-plus-info-ouvert-pieds-page").css({"display" : "block"});
                      $(".lien-plus-info-ouvert-pieds-page").addClass("plus-info-ouvert");
                      return false;
                });

                $(".masquer-info").click(function() {
                      $(".plus-info-detail").hide();
                      $(".lien-plus-info").css("display","block");
                      $(".lien-plus-info-ouvert").removeClass("plus-info-ouvert");
                      $(".lien-plus-info-ouvert").css({"display" : "none"});
                      $(".afficher-bloc-info").css("display","block");
                      $(".masquer-bloc-info").css("display","none");
                      $(".accroche-plus-info").removeClass("plus-info-ouvert");
                      $(".plus-info").removeClass("plus-info-ouvert");
                      $(".plus-info-pictos-pieds-page").css("display","block");
                      $(".lien-plus-info-ouvert-pieds-page").css("display","none");

                      return false;
                });

  var myDiapo = $(".diaporama1").jDiaporama({
    delay:5,
    width:320,
    height:241,
    paused:true,
        transition:"fade"
  });

  $("#prev").click(function(){
    myDiapo.data("jDiaporama").prev();
  })

  $("#next").click(function(){
    myDiapo.data("jDiaporama").next();
  })

  $("#decreaseSlices").click(function(){
    nbSlices = myDiapo.data("jDiaporama").getOption('nbSlices');
    myDiapo.data("jDiaporama").changeOption("nbSlices", --nbSlices);
  })

  $(".diaporama1").parent().parent().parent().bind("jDiaporama:pause", function(event, pause){
    if(!pause){
                        $("#Header").css("display","none");
                        $(".fiche-interet h1").css("display","none");
                        $("#Main .accroche").css("display","none");
                        $("#Main .bloc-plus-image").css("display","none");
                        $("#Main .plus-info").css("display","none");
                        $("#Main .info").css("display","none");
                        $("#Main .bloc-carte-google").css("display","none");
                        $("#breadcrumbs").css("display","none");
                        $(".recherche.accueil").css("display","none");
                        $(".copyright").css("display","none");
                        $(".pieds-page .normal").css("display","none");
                        $(".pieds-page .media-diaporama").css({"display":"block"});

      $("#togglePause").css("background","#98E2CC url('../images/diapo-play.png') no-repeat center;");

      }
    else{
      $("#togglePause").css("background","#98E2CC url('../images/diapo-pause.png') no-repeat center;");

      }
  })

  $("#togglePause").click(function(){
    myDiapo.data("jDiaporama").pauseSlider();

  })

   $(".video").hover(function(){
            if($(this).find(".jp-play").is(":visible")){
    $(this).find(".jp-video-play").show();
             } else{
               $(this).find(".jp-video-play").hide();
             }
  },function(){

              $(this).find(".jp-video-play").hide();
  })

   $(".diapo-ferme").click(function(){
         $("#Header").css("display","block");
                     $(".fiche-interet h1").css("display","block");
                     $("#Main .accroche").css("display","block");
                     $("#Main .bloc-plus-image").css("display","block");
                     $("#Main .plus-info").css("display","block");
                     $("#Main .info").css("display","block");
                     $("#Main .bloc-carte-google").css("display","block");
                     $("#breadcrumbs").css("display","block");
                     $(".recherche.accueil").css("display","block");
                     $(".copyright").css("display","block");
                     $(".pieds-page .normal").css("display","block");
                     $(".pieds-page .media-diaporama").css({"display":"none"});
                     myDiapo.data("jDiaporama").pauseSlider();
     });

	var optionsPhotoSwipe = {
			enableMouseWheel: false
			, enableKeyboard: false
			, autoStartSlideshow: true
			, captionAndToolbarOpacity: 1			
			, captionAndToolbarAutoHideDelay: 0
	  };

	var myPhotoSwipe = $("#Gallery a").photoSwipe(optionsPhotoSwipe);

	if($('#diaporama-view').length > 0)
		myPhotoSwipe.show(0);

	$(".ps-toolbar-close").click(function(){
		window.history.back();
		return false;
	})


});
